const countDownDate=new Date("Sept 21, 2020").getTime(),timerFunction=setInterval(()=>{const currentDate=(new Date).getTime(),finalTime=countDownDate-currentDate;if(finalTime>0){const days=Math.floor(finalTime/864e5),hours=Math.floor(finalTime%864e5/36e5),minutes=Math.floor(finalTime%36e5/6e4),seconds=Math.floor(finalTime%6e4/1e3);document.getElementById("days").innerHTML=days,document.getElementById("hours").innerHTML=hours,document.getElementById("minutes").innerHTML=minutes,document.getElementById("seconds").innerHTML=seconds}else clearInterval(timerFunction),document.getElementById("timer").innerHTML="It Is Time"},1e3);